from pages.entPage.business_member import BussinessMemberfrom pages.basePage import Action# ============================#    我的企业页面用例# ============================class Testbussmember:    def test_act_member_manage(self, driver):        '''验证进入成员管理功能'''        BussinessMember(driver).act_member_manage()        Action(driver).sleep()    def test_edit_temp_name(self, driver):        '''验证进入成员管理--修改团队名称'''        Bm = BussinessMember(driver)        Bm.test_edit_temp_name()        Action(driver).sleep()    def test_invite_member(self, driver):        '''验证进入成员管理--邀请成员'''        Bm = BussinessMember(driver)        Bm.test_invite_member()        Action(driver).sleep()    def test_send_invite_link(self, driver):        '''验证进入成员管理--发送邀请链接'''        Bm = BussinessMember(driver)        Bm.test_send_invite_link()        Action(driver).sleep()    def test_add_one_member(self, driver):        '''验证进入成员管理--添加成员'''        Bm = BussinessMember(driver)        Bm.test_add_one_member()        Action(driver).sleep()    def test_add_more_member(self, driver):        '''验证进入成员管理--批量添加成员'''        Bm = BussinessMember(driver)        Bm.test_add_more_member()        Action(driver).sleep()    def test_edit_member(self, driver):        '''验证进入成员管理--编辑成员信息'''        Bm = BussinessMember(driver)        Bm.test_edit_member("新备注")        Action(driver).sleep()    def test_remove_member(self, driver):        '''验证进入成员管理--移出成员信息'''        Bm = BussinessMember(driver)        Bm.test_remove_member()        Action(driver).sleep()