"""Author  : MiloTime    : 2022/5/25 16:36Desc    : 音乐模块执行用例"""import timefrom pages.sc_copyright.sc_music import ScMusic# 团队身份class TestMusic:    def test_into_music(self, driver):        '''测试团队身份进入音乐列表'''        ScMusic(driver).test_into_music()        time.sleep(1)    def test_act_music_detail(self, driver):        '''测试团队身份进入音乐详情'''        ScMusic(driver).act_music_detail(driver)        time.sleep(1)    def test_play_music(self, driver):        '''测试团队身份在音乐详情【播放】音乐'''        ScMusic(driver).play_music()        time.sleep(1)    def test_music_detail_coll(self, driver):        '''测试团队身份在音乐详情【收藏】音乐'''        ScMusic(driver).music_detail_coll()        time.sleep(1)    def test_recommended_music(self, driver):        '''测试团队身份在音乐详情点击【推荐音乐】'''        ScMusic(driver).recommended_music(driver)        time.sleep(1)    def test_click_keyword(self, driver):        '''测试团队身份在音乐详情点击关键词'''        ScMusic(driver).test_music_click_keyword()        time.sleep(2)    def test_search_music(self, driver):        '''测试团队身份搜索音乐'''        ScMusic(driver).search_music()        time.sleep(1)    def test_AddCollect(self, driver):        '''测试团队身份在音乐列表收藏音乐'''        ScMusic(driver).AddCollect()        time.sleep(1)    def test_CancelCollect(self, driver):        '''测试团队身份在音乐列表取消收藏音乐'''        ScMusic(driver).CancelCollect()        time.sleep(1)    def test_musicList_play(self, driver):        '''测试团队身份在音乐列表播放音乐'''        ScMusic(driver).test_musicList_play()        time.sleep(1)    def test_musicList_pause(self, driver):        '''测试团队身份在音乐列表取消播放音乐'''        ScMusic(driver).test_musicList_pause()        time.sleep(1)